String.prototype.replaceAll=function(b,a,c){return this.replace(new RegExp(b.replace(/([\,\!\\\^\$\{\}\[\]\(\)\.\*\+\?\|\<\>\-\&])/g,function(d){return"\\"+d}),"g"+(c?"i":"")),a)};var lang;if(navigator.userAgent&&(lang=navigator.userAgent.match(/android.*\W(\w\w)-(\w\w)\W/i))){lang=lang[1]}else{lang=(navigator.language||navigator.userLanguage).substr(0,2)}var i18n={};i18n.dict=[];i18n.translate=function(){if(lang=="en"){document.body.innerHTML=document.body.innerHTML.replace(/\{\{/g,"").replace(/\}\}/g,"");return}var a=document.body.innerHTML;for(var b in i18n.dict[lang]){a=a.replaceAll("{{"+b+"}}",i18n.dict[lang][b])}document.body.innerHTML=a};i18n._=_=function(b,a){if(lang=="en"){return b}b=i18n.dict[lang][b];if(a){for(var c=0;c<a.length;c++){b=b.replace("%s",a[c])}}return b};